import React from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

import { useState } from 'react';
import axios from "../axios/axios";
function Login() {
    const [email,setEmail] = useState()
    const [password,setPassword] = useState()
    const [error,setError] =useState()
    const handleSubmit = (e) => {
        e.preventDefault();
    }
  return <div class="box">
	  <div>
      {error ? <div class="alert alert-primary" role="alert">
      {error}
</div> : null }
  </div>
  <div class="inner-box">

      <form onSubmit={e => { handleSubmit(e) }} >

      <h2>Login Here</h2>

      <input type="t" name="username" value={email} onChange={e=> setEmail(e.target.value)} placeholder="Your Email" autoComplete="on" required/>
      <input type="password" password="password" value={password} onChange={e=> setPassword(e.target.value)} placeholder="Password" required/>

      <p>

      <input type="checkbox" /><span> Keep me Signed In</span>
      <p style={{textAlign:"center"}}>
          <Link to="/ResetPassword">
          <span class="forgot" >Forgot Password?</span>
              </Link>
      </p>


      </p>

      <input type="submit" value="LogIn" />

      <p style={{textAlign:"center"}}>
          <Link to="/Register">
          <span>Not a member? </span> Sign Up
              </Link>
      </p>

      </form>

  </div>

</div>
}

export default Login;
