import React from "react";
import { Link } from "react-router-dom";
import { useNavigate, useParams } from "react-router-dom";

import { useState } from 'react';
import axios from "../axios/axios";
function ChangePassword() {
    const navigate = useNavigate();
    let { id } = useParams();
    const [password,setPassword] = useState()
    const [confirmPassword,setconfirmPassword] = useState()
    const [error,setError] =useState()
    const change = {
        password:password,
        confirmPassword: confirmPassword
    }
    function  setCookie (cname, cvalue, exdays) {
        const d = new Date()
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000))
        const expires = 'expires=' + d.toUTCString()
        document.cookie = cname + '=' + cvalue + ';' + expires + ';path=/'
      }
    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post(`users/updatePasswordAfterReceiveEmail/${id}`,change,{
            headers:{
                'Content-Type': 'application/json'
            }
        }).then(response => {
            if(response.data.succes === false)
            {
                setError(response.data.message)
            }
            else {

                // eslint-disable-next-line no-cond-assign
                if(response.data.boolean === 0)
                {

                    setCookie("userId",response.data.userId,3)
                    setCookie("token",response.data.token)
                    navigate('/Admin')
                }
                else {
                    setCookie("userId",response.data.userId,3)
                    setCookie("token",response.data.token)
                    navigate('/Home')
                }
            }
        })
    }
  return <div class="box">

  <div class="inner-box">
  <div>
      {error ? <div class="alert alert-primary" role="alert">
      {error}
</div> : null }
  </div>
      <form onSubmit={e => { handleSubmit(e) }} >

      <h2>Welcome!Choose your new paswword</h2>

      <input type="password" name="" value={password} onChange={e=> setPa(e.target.value)} placeholder="Your Email" autoComplete="on" required/>
      <input type="password" password="password" value={password} onChange={e=> setPassword(e.target.value)} placeholder="Password" required/>

      <p>

      <input type="checkbox" /><span> Keep me Signed In</span>
      <p style={{textAlign:"center"}}>
          <Link to="/ResetPassword">
          <span class="forgot" >Forgot Password?</span>
              </Link>
      </p>


      </p>

      <input type="submit" value="LogIn" />

      <p style={{textAlign:"center"}}>
          <Link to="/Register">
          <span>Not a member? Sign Up </span> 
              </Link>
      </p>

      </form>

  </div>

</div>
}

export default ChangePassword;
